mapper.select:
select count(*) as totCnt
from statistc.requestinfo ri
where left(ri.createDate, 2) = #{year};

select count(*) as totCnt
from statistc.requestinfo ri
where mid(ri.createDate, 3, 2) = #{month};

select count(*) as totCnt
from statistc.requestinfo ri
where mid(ri.createDate, 3, 4) = #{daily};

select count(*) as totCnt
from statistc.requestinfo ri
where departmentCode = #{depCode};
